import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
// import SearchBar from "./comp/SearchBar.js";


const apiKey = "8df56f673f0e663f365888c9eb52029f";







function App() {

  const [input, setInput] = useState('');

  function updateCities() {

    console.log(input);
    let apiCall = `https://api.openweathermap.org/geo/1.0/direct?q=${input},,US&limit=5&appid=${apiKey}`;
    fetch(apiCall)
      .then((response) => response.json())
      .then((data) => { console.log(data) })
  }

  useEffect(updateCities)


  const [cities, setCities] = useState('');


  function renderCities() {
    const listItems = cities.map((number) => {
      <li key={number.toString()}>
        {number}
      </li>
    })
    return (
      <ul>{listItems}</ul>

    )
  }

  return (
    <div className="App">
      <div id="weather-section">
      </div>
      <div id="city-section">
        <div >
          <input id="input-field" value={input} onChange={() => setInput(event.target.value)} ></input>
        </div>
        {renderCities}
      </div>
    </div>
  );
}

export default App;
