import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import { useEffect } from "react";
// import SearchBar from "./comp/SearchBar.js";


const apiKey = "8df56f673f0e663f365888c9eb52029f";

function App() {

  function onClickHander() {
    let input = document.getElementById("input-field").value;
    let apiCall = `https://api.openweathermap.org/geo/1.0/direct?q=${input},,US&limit=5&appid=${apiKey}`;
    fetch(apiCall)
      .then((response) => response.json())
      .then((data) => { 
        console.log(data);
        data = [{name:"Austin", state:"TX"}, {name:"Austin", state:"FL"}];
        setCities(data) 
      })
  }

  const [cities, setCities] = useState('');


  return (
    <div className="App">
      <div id="weather-section">
      </div>
      <div id="city-section">
        <div >
          <input id="input-field"></input>
          <button id="search-button" onClick = {onClickHander}>Search</button>
        </div>
        <RenderCities cities = {cities}/>
      </div>
    </div>
  );
}

function RenderCities(props) {
  const cityNames = [];
  for (var i = 0; i < props.cities.length; i++) {
    const city = props.cities[i];
    cityNames[i] = city.name + " " +  city.state
  }
  console.log(cityNames);
  const output = cityNames.map((name) => {
    <li key = {name}>
      {name}
    </li>
  })
  return (
   <ul>{cityNames}</ul>
  )
}

export default App;
