import React, { useState } from "react";

export default function SearchBar(props) {
    function renderCities(searchInput) {
        console.log(searchInput);
        let apiCall = `https://api.openweathermap.org/geo/1.0/direct?q=${searchInput},,US&limit=5&appid=${props.apiKey}`;
        // calls the API

    }

    const [input, setInput] = useState('');

    const updateCities = event => {
        setInput(event.target.value);
        fetch(apiCall)
            .then((response) =>
                // after recieving a response, take the response from the server and convert it to JSON 
                response.json()
            )
            .then((data) => {
                // after recieving the converted JSON data, pass the JSON to the renderSearchResults() function

                console.log(data);
            });

    };

    return (
        <div >
            <input id="input-field" value = {input} onChange={updateCities} ></input>
            {/* <button id="search-button" onClick={() => renderCities(searchBar.value)}>Search</button> */}
        </div>
    )
}
