import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import { useEffect } from "react";
// import SearchBar from "./comp/SearchBar.js";


const apiKey = "8df56f673f0e663f365888c9eb52029f";

function App() {

  function onClickHander() {
    let input = document.getElementById("input-field").value;
    let apiCall = `https://api.openweathermap.org/geo/1.0/direct?q=${input},,US&limit=5&appid=${apiKey}`;
    fetch(apiCall)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // data = [{ name: "Austin", state: "TX" }, { name: "Austin", state: "FL" }];
        setCities(data)
      })
  }

  const [cities, setCities] = useState('');

  function Cities() {
    const output = []
    for (var i = 0; i < cities.length; i++) {
      const city = cities[i];
      const name = city.name + " " + city.state
      output[i] = <li key={"" + city.lon + city.lat} className="city-name" onClick={() => getWeatherData(name, city.lon, city.lat)}>
        {name}
      </li>
    }
    return (
      <ul style={{ textAlign: "left" }}>{output}</ul>
    )
  }

  function getWeatherData(cityName, lon, lat) {
    console.log(cityName + " " + lon + " " + lat);
  }

  const [weatherData, setWeatherData] = useState('');

  function WeatherData() {
    if (weatherData !== '') {
      let apiCall = "https://api.openweathermap.org/data/2.5/weather?lat=" + city.lat + "&lon=" + city.lon + "&appid=" + apiKey + "&units=imperial";
      // calls the API
      fetch(apiCall)
        .then((response) =>
          // after recieving a response, take the response from the server and convert it to JSON 
          response.json()
        )
        .then((data) => {
          // after recieving the converted JSON data, pass the JSON to the renderSearchResults() function
          let currentWeather = data;

          let apiCall = "https://api.openweathermap.org/data/2.5/air_pollution?lat=" + city.lat + "&lon=" + city.lon + "&appid=" + apiKey;
          // calls the API
          fetch(apiCall)
            .then((response) =>
              // after recieving a response, take the response from the server and convert it to JSON 
              response.json()
            )
            .then((data) => {
              // after recieving the converted JSON data, pass the JSON to the renderSearchResults() function
              let polution = data;

              let apiCall = "https://api.openweathermap.org/data/2.5/forecast?lat=" + city.lat + "&lon=" + city.lon + "&appid=" + apiKey + "&units=imperial";
              // calls the API
              fetch(apiCall)
                .then((response) =>
                  // after recieving a response, take the response from the server and convert it to JSON 
                  response.json()
                )
                .then((data) => {
                  // after recieving the converted JSON data, pass the JSON to the renderSearchResults() function
                  let forcast = data;
                  renderWeather(city, currentWeather, polution, forcast);
                });
            });
        });
    }
  }


  return (
    <div className="App">
      <div id="weather-section">
        <WeatherData />
      </div>
      <div id="city-section">
        <div id="search-section">
          <input id="input-field"></input>
          <button id="search-button" onClick={onClickHander}>Search</button>
        </div>
        <div><Cities /></div>

      </div>
    </div>
  );
}



export default App;
